}
/// Dynamic subcommand.
#[derive(PartialEq, Debug)]
struct Dynamic {
  name: String
}
impl DynamicSubCommand for Dynamic {
  fn commands() -> &'static [&'static CommandInfo] {
    static RET: OnceCell<Vec<&'static CommandInfo>> = OnceCell::new();
